<script>
export default {
  props: {
    mapList: {
      type: Array,
    },
    areaList: {
      type: Array,
    },
  },
  data() {
    return {
      baseUrl: process.env.imgBaseUrl,
      section: [],
      activeIndex: null,
      selectIndex: null,
      childrenMapList: [],
      areaId: null,
    }
  },
  created() {
    // if(this.areaList)this.areaId = this.areaList[0].id
    // this.handleChane();
  },
  watch: {
    areaList: {
      immediate: true,
      handler(n, o) {
        if (n && n[0]) this.areaId = n[0].id
      },
    },
    areaId: {
      immediate: true,
      handler(n, o) {
        if (n) this.handleChane(n)
      },
    },
  },
  methods: {
    // 社媒icon点击
    handleSocialItem(type, map) {
      console.log(type)
      console.log(map.name)
      this.$gtag.event('home_map', { type: 'click', region: map.name, link: type })
    },
    // 鼠标点击标记点
    handlePinClick(map) {
      console.log('点击', map.name)
      this.$gtag.event('home_map', { type: 'hover', region: map.name })
    },
    // 鼠标经过标记点
    handlePinMouseOver(map) {
      console.log('经过', map.name)
      this.$gtag.event('home_map', { type: 'click', region: map.name })
    },
    mouseenterActive(index) {
      this.activeIndex = index
    },
    mouseleaveActive() {
      this.activeIndex = null
    },
    mouseClick(index) {
      this.selectIndex = index
    },
    closeWindow(e) {
      this.selectIndex = null
    },
    handleChane(areaId) {
      this.$api.home.getMap({ areaId }).then((res) => {
        this.childrenMapList = res.data
      })
    },
  },
}
</script>

